mapper.update:
UPDATE community
SET  title =#{title},content=#{content},writedate=sysdate,tag=#{tag}
WHERE communityno=#{communityno}

mapper.select:
SELECT communityno,title,content,vcnt,rcnt,writedate,tag,memberno,ctypeno, r FROM( SELECT communityno,title,content,vcnt,rcnt,writedate,tag,memberno,ctypeno, rownum as r FROM ( SELECT communityno,title,content,vcnt,rcnt,writedate,tag,memberno,ctypeno FROM community
WHERE UPPER(title)LIKE '%'||UPPER(#{word})||'%'OR UPPER(tag)LIKE '%' || UPPER(#{word})
ORDER BY communityno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
