mapper.update:
UPDATE community
SET  title =#{title},content=#{content},writedate=sysdate,tag=#{tag}
WHERE communityno=#{communityno}

mapper.select:
SELECT communityno,title,content,vcnt,rcnt,writedate,tag,memberno,ctypeno, r FROM( SELECT communityno,title,content,vcnt,rcnt,writedate,tag,memberno,ctypeno, rownum as r FROM ( SELECT c.communityno,c.title,c.content,c.vcnt,c.rcnt,c.writedate,c.tag,c.memberno,c.ctypeno,ca.cano, ca.filename, ca.filesize, ca.thumbfile FROM community c LEFT OUTER JOIN communityattachment ca ON c.communityno = ca.communityno
WHERE UPPER(title)LIKE '%'||UPPER(#{word})||'%'OR UPPER(tag)LIKE '%' || UPPER(#{word})
) ) WHERE r >= #{start_num} AND r <= #{end_num}

mapper.update:
UPDATE community 
SET VCNT = VCNT + 1
WHERE communityno=#{communityno}

UPDATE community 
SET RCNT = RCNT + 1
WHERE communityno=#{communityno}
