mapper.select:
SELECT rcnt,rcntdate,communityno,memberno
FROM communitylike
WHERE memberno=#{memberno} AND communityno=#{communityno}

mapper.update:
UPDATE communitylike
SET rcnt = #{rcnt} + 1
WHERE memberno=#{memberno} AND communityno=#{communityno}

UPDATE communitylike
SET rcnt = 0
WHERE memberno=#{memberno} AND communityno=#{communityno}

mapper.select:
SELECT COUNT(rcnt) as cnt 
FROM communitylike
WHERE memberno=#{memberno} AND communityno=#{communityno}
