mapper.select:
SELECT cname, raddress, lattitude, longitude, addr_code,phone,date, operatingtime,pa
FROM culturefacility
WHERE cultureno = #{cultureno}

mapper.update:
UPDATE culturefacility
SET cname = #{cname}, raddress = #{raddress}, lattitude = #{lattitude}, longitude = #{longitude}, addr_code = #{addr_code},
    phone = #{phone}, operatingtime = #{operatingtime}, pa = #{pa}
WHERE cultureno = #{cultureno}
