mapper.select:
SELECT masterloginno, ip, conndate, masterno, mastername, masterid, r FROM ( SELECT masterloginno, ip, conndate, masterno, mastername, masterid, rownum AS r FROM ( SELECT lg.masterloginno, lg.ip, lg.conndate, lg.masterno, mb.mastername, mb.masterid FROM masterlogin lg JOIN master mb ON lg.masterno = mb.masterno OR lg.masterno IS NULL
WHERE (UPPER(lg.ip) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(lg.conndate) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(mb.mastername) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(mb.masterid) LIKE '%' || UPPER(#{word}) || '%') AND lg.masterno != 0
ORDER BY lg.masterloginno DESC ) ) WHERE r > = #{start_num} AND r < = #{end_num}
