mapper.select:
select count(Ano)
from MATEAPPLY
where MEMBERNO = #{memberNo} and MCOMMUNITYNO = #{mCommunityNo}

mapper.delete:
delete
from MATEAPPLY
where MEMBERNO = #{memberNo} and MCOMMUNITYNO = #{mCommunityNo}

mapper.select:
select count(*)
from MATEAPPLY
where MEMBERNO = #{memberNo}

select count(*)
from MATEAPPLY
where MCOMMUNITYNO = #{mCommunityNo}

mapper.update:
update MATEAPPLY
set ASTATUS = 'ACCEPTED'
where ANO = #{aNo}

update MATEAPPLY
set ASTATUS = 'DENIED'
where ANO = #{aNo}

mapper.select:
select count(ANO)
from MATEAPPLY
where ASTATUS = 'ACCEPTED' and MCOMMUNITYNO = #{mCommunityNo}
