mapper.select:
SELECT MCOMMUNITYNO, TITLE, CONTENT, VIEWCNT, STARTINGP, STARTINGDETAIL, WALKINGM, WDATE, ASSEMBLETIME, STATUS, SEARCHTAG, PETTYPENO, MEMBERNO, r FROM ( SELECT MCOMMUNITYNO, TITLE, CONTENT, VIEWCNT, STARTINGP, STARTINGDETAIL, WALKINGM, WDATE, ASSEMBLETIME, STATUS, SEARCHTAG, PETTYPENO, MEMBERNO, rownum AS r FROM ( SELECT MCOMMUNITYNO, TITLE, CONTENT, VIEWCNT, STARTINGP, STARTINGDETAIL, WALKINGM, WDATE, ASSEMBLETIME, STATUS, SEARCHTAG, PETTYPENO, MEMBERNO FROM MATECOMMUNITY
WHERE UPPER(title) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(STARTINGP) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(SEARCHTAG) LIKE '%' || UPPER(#{searchWord}) || '%'
ORDER BY MCOMMUNITYNO desc ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT MCOMMUNITYNO, TITLE, CONTENT, VIEWCNT, STARTINGP, STARTINGDETAIL, WALKINGM, WDATE, ASSEMBLETIME, STATUS, SEARCHTAG, PETTYPENO, MEMBERNO, r FROM ( SELECT MCOMMUNITYNO, TITLE, CONTENT, VIEWCNT, STARTINGP, STARTINGDETAIL, WALKINGM, WDATE, ASSEMBLETIME, STATUS, SEARCHTAG, PETTYPENO, MEMBERNO, rownum AS r FROM ( SELECT MCOMMUNITYNO, TITLE, CONTENT, VIEWCNT, STARTINGP, STARTINGDETAIL, WALKINGM, WDATE, ASSEMBLETIME, STATUS, SEARCHTAG, PETTYPENO, MEMBERNO FROM MATECOMMUNITY WHERE PETTYPENO=#{petTypeNo}
AND (UPPER(title) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(STARTINGP) LIKE '%' || UPPER(#{searchWord}) || '%' OR UPPER(SEARCHTAG) LIKE '%' || UPPER(#{searchWord}) || '%')
ORDER BY WDATE desc ) )WHERE r >= #{start_num} AND r <= #{end_num}

SELECT MCOMMUNITYNO, TITLE, CONTENT, VIEWCNT, STARTINGP, STARTINGDETAIL, WALKINGM, WDATE, ASSEMBLETIME, STATUS, SEARCHTAG, PETTYPENO, MEMBERNO
FROM MATECOMMUNITY
WHERE MCOMMUNITYNO=#{mCommunityNo}

mapper.update:
UPDATE MATECOMMUNITY
SET VIEWCNT= VIEWCNT+1
WHERE MCOMMUNITYNO=#{mCommunityNo}

UPDATE MATECOMMUNITY
SET STATUS = 1
WHERE MCOMMUNITYNO=#{mCommunityNo}

UPDATE MATECOMMUNITY
SET STATUS = 1
WHERE MCOMMUNITYNO=#{mCommunityNo}
