mapper.select:
select rno, reviewcomment, reviewgrade, rdate, memberno, mcommunityno, r
from
(
select rno, reviewcomment, reviewgrade, rdate, memberno, mcommunityno, ROWNUM as r
from
(
select rno, reviewcomment, reviewgrade, rdate, memberno, mcommunityno
from MATEREVIEW
where MEMBERNO = #{memberrNo}
order by RNO
)
)
WHERE r >= #{start_num} AND r <= #{end_num}

select rno, reviewcomment, reviewgrade, rdate, memberno, mcommunityno, r
from
(
select rno, reviewcomment, reviewgrade, rdate, memberno, mcommunityno, ROWNUM as r
from
(
select rno, reviewcomment, reviewgrade, rdate, memberno, mcommunityno
from MATEREVIEW
where MCOMMUNITYNO = #{mCommunityNo}
order by RNO
)
)
WHERE r >= #{start_num} AND r <= #{end_num}
