mapper.update:
UPDATE memberprofile
SET mprofileno=#{mprofileno}, file1=#{file1}, file1saved=#{file1saved}, thumbfile=#{thumbfile}, filesize=#{filesize}, memberno=#{memberno}
WHERE memberno = #{memberno} AND mprofileno=#{mprofileno}

mapper.select:
SELECT mprofileno, file1, file1saved, thumbfile, filesize, memberno
FROM memberprofile
WHERE memberno = #{memberno}
