mapper.update:
UPDATE notice
SET title=#{title}, notice=#{notice}, nfile=#{nfile}
WHERE noticeno = #{noticeno}

mapper.select:
SELECT noticeno, title, notice, nfile, noticedate, passwd ,masterno
FROM notice
WHERE noticeno =#{noticeno}

SELECT COUNT(*) as cnt 
FROM notice
WHERE noticeno = #{noticeno} AND passwd=#{passwd}

SELECT noticeno, title, notice, nfile, noticedate, passwd ,masterno, r FROM ( SELECT noticeno, title, notice, nfile, noticedate, passwd ,masterno, rownum as r FROM ( SELECT noticeno, title, notice, nfile, noticedate, passwd ,masterno FROM notice
WHERE (UPPER(title) Like '%' || UPPER(#{word}) || '%' OR UPPER(notice) Like '%' || UPPER(#{word}) || '%')
ORDER BY noticeno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
