mapper.select:
select petno, petname, petbirth, petgender, petdetail, pettypeno, memberno
from PET
where PETNO = #{petNo}

select petno, petname, petbirth, petgender, petdetail, pettypeno, memberno
from PET
where MEMBERNO = #{memberNo}

select count(*)
from PET
where MEMBERNO = #{memberNo}

mapper.update:
UPDATE PET
SET PETNAME = #{petName}, PETDETAIL = #{petDetail}
WHERE PETNO = #{petNo}
